-- Long Operations Graph with HTML Output
-- 01/29/2009

DECLARE
  NumOperations NUMBER :=0;
BEGIN

  DBMS_OUTPUT.PUT_LINE('<FONT SIZE=3 FACE="Courier">');

  FOR c1 IN (SELECT MESSAGE, ROUND(sofar/totalwork*100,0) as pct
               FROM v$session_longops
         WHERE sofar <> totalwork
      ORDER BY target, sid) 
  LOOP

    DBMS_OUTPUT.PUT_LINE('<TABLE BORDER="1" BGCOLOR="#EEEEEE" WIDTH="100%">');
    DBMS_OUTPUT.PUT_LINE('<TR>');
    DBMS_OUTPUT.PUT_LINE('<TD WIDTH=100>Operation:</TD>');
    DBMS_OUTPUT.PUT_LINE('<TD>'||c1.MESSAGE || '</TD>');
    DBMS_OUTPUT.PUT_LINE('</TR>');
    DBMS_OUTPUT.PUT_LINE('<TR>');
    DBMS_OUTPUT.PUT_LINE('<TD>Progress:</TD>');

    DBMS_OUTPUT.PUT_LINE('  <TABLE BORDER="1" BGCOLOR="RED" WIDTH="'||c1.pct||'%">');
    DBMS_OUTPUT.PUT_LINE('  <TR><TD>'||c1.pct||'%</TD></TR>');
    DBMS_OUTPUT.PUT_LINE('  </TABLE>');

    DBMS_OUTPUT.PUT_LINE('</TD></TR></TABLE>');

    DBMS_OUTPUT.PUT_LINE('<BR>');
    NumOperations := NumOperations + 1;
  END LOOP;

  IF NumOperations = 0 THEN
    DBMS_OUTPUT.PUT_LINE('No Long Operations Active');
  END IF;

  DBMS_OUTPUT.PUT_LINE('</FONT>');


END;

declare
dbid number;
instance_id number;
start_id number;
end_id number;
begin
dbms_output.enable(1000000);
select max(snap_id)-24,max(snap_id) into start_id,end_id from dba_hist_snapshot;
select dbid into dbid from v$database;
select instance_number into instance_id from v$instance;

dbms_output.put_line('<PRE>');
for rc in ( select output from 
   table(dbms_workload_repository.awr_report_text(dbid,instance_id,start_id,end_id)) 
           ) loop
  -- dbms_output.put_line('<BR>');
   dbms_output.put_line(rc.output);
end loop;
dbms_output.put_line('</PRE>');
end;

DECLARE
  -- David Mann
  -- http://ba6.us
  -- This code is from http://ba6.us/5_in_5_No_1
  --
  -- History:
  -- 2014-09-24 DMann Initial release of v1

  maxAvgElapsedPerExec NUMBER;
  maxExecCount NUMBER;
  maxTotalElapsedPerPeriod NUMBER;

  sqlid VARCHAR2(15) := ''||:statement_sql_id;

  dbname VARCHAR2(20);

  FUNCTION dec2hex (N in number) RETURN varchar2 IS
  hexval varchar2(64);
  N2     number := N;
  digit  number;
  hexdigit  char;
BEGIN
  while ( N2 > 0 ) loop
     digit := mod(N2, 16);
     if digit > 9 then 
       hexdigit := chr(ascii('A') + digit - 10);
     else
       hexdigit := to_char(digit);
     end if;
     hexval := hexdigit || hexval;
     N2 := trunc( N2 / 16 );
  end loop;
  return hexval;
END dec2hex;

FUNCTION ColorHash (plan_hash_value in number) return varchar2 IS
  retval VARCHAR2(6);
BEGIN
  retval := SUBSTR(dec2hex(substr(plan_hash_value,1,3)),1,2);
  retval := retval || SUBSTR(dec2hex(substr(plan_hash_value,4,3)),1,2);
  retval := retval || SUBSTR(dec2hex(substr(plan_hash_value,7,3)),1,2);

return retval;
END;

BEGIN
  dbms_output.enable(100000000);

  -- Find upper bounds 
  SELECT MAX((elapsed_time_total/1000000)/executions_total) as MAX_AVG_ELAPSED_PER_EXEC, 
         MAX(executions_total) AS MAX_EXECUTION_COUNT,
         MAX(elapsed_time_total) as MAX_TOTAL_ELAPSED_PER_PERIOD
    INTO maxAvgElapsedPerExec, maxExecCount, maxTotalElapsedPerPeriod
  FROM dba_hist_sqlstat
  JOIN dba_hist_snapshot
  ON dba_hist_sqlstat.snap_id=dba_hist_snapshot.snap_id
    and dba_hist_sqlstat.instance_number=dba_hist_snapshot.instance_number
  WHERE sql_id              IN (TRIM(sqlid))
  AND executions_total       > 0;

  -- Main table
  select name into dbname from v$database;
  DBMS_OUTPUT.PUT_LINE('<H1>Execution Plan Color Map for '|| sqlid ||'@'|| dbname ||'</H1>');

  DBMS_OUTPUT.PUT_LINE('<TABLE BORDER="0" BGCOLOR="#EEEEEE" WIDTH="100%">');
  -- Header
  DBMS_OUTPUT.PUT_LINE('<TR>');
  DBMS_OUTPUT.PUT_LINE('<TD WIDTH="8%">Time Period</TD>');
--  DBMS_OUTPUT.PUT_LINE('<TD WIDTH="8%">Count (Max '||maxExecCount||') :</TD>');
  DBMS_OUTPUT.PUT_LINE('<TD WIDTH="8%">Total Elapsed Time Per Period (Max '||ROUND(maxTotalElapsedPerPeriod)||'s) :</TD>');
  DBMS_OUTPUT.PUT_LINE('<TD WIDTH="76%">Avg Time Per Exec (Max '||ROUND(maxAvgElapsedPerExec)||'s) :</TD>');
  DBMS_OUTPUT.PUT_LINE('</TR>');

  FOR cur IN (
    SELECT end_interval_time,
           sql_id,
           plan_hash_value,
           ROUND(elapsed_time_total/1000000,2)                    AS "ElapsedTotal",
           executions_total,
           --px_servers_execs_total AS "PXServers",
           ROUND((elapsed_time_total/1000000)/executions_total,2) AS ElapsedPerExec,
           elapsed_time_total
           --DBA_HIST_SNAPSHOT.INSTANCE_NUMBER as INST_NUM, 
           --dba_hist_sqlstat.*
      FROM dba_hist_sqlstat
      JOIN dba_hist_snapshot
        ON dba_hist_sqlstat.snap_id=dba_hist_snapshot.snap_id
           AND dba_hist_sqlstat.instance_number=dba_hist_snapshot.instance_number
     WHERE sql_id              IN (TRIM(sqlid))
       AND executions_total       > 0
     ORDER BY DBA_HIST_SNAPSHOT.end_interval_time DESC,
              DBA_HIST_SNAPSHOT.INSTANCE_NUMBER)
  LOOP

    DBMS_OUTPUT.PUT_LINE('<TR>');

    -- Date field contents
    DBMS_OUTPUT.PUT_LINE('<TD><p style="font-size:10px">'||TO_CHAR(cur.END_INTERVAL_TIME,'YYYY-MM-DD HH24:MI')||'</P></TD>');
    -- Count field contents
    --DBMS_OUTPUT.PUT_LINE('<TD><p style="font-size:10px" align="RIGHT">'||
    --  '<TABLE  cellpadding=0 BORDER="0" BGCOLOR="#000000" WIDTH="'||ROUND(cur.executions_total/maxExecCount*100)||
    --  '%"><TR><TD></TD></TR></TABLE>'||'</TD>');
    -- Total Time
    DBMS_OUTPUT.PUT_LINE('<TD><p style="font-size:10px" align="RIGHT">'||
      '<TABLE  cellpadding=0 BORDER="0" BGCOLOR="#000000" WIDTH="'||ROUND(cur.elapsed_time_total/maxTotalElapsedPerPeriod*100)||
      '%"><TR><TD></TD></TR></TABLE>'||'</TD>');

    -- Time/Plan field contents
    DBMS_OUTPUT.PUT_LINE('<TD><p style="font-size:10px">'||
      '<TABLE  cellpadding=0 BORDER="0" BGCOLOR="#'||
      ColorHash(cur.plan_hash_value)||'" WIDTH="'||
      ROUND(cur.ElapsedPerExec/maxAvgElapsedPerExec*100)||'%"><TR><TD>'||
      cur.plan_hash_value||'-123</P></TD></TR></TABLE></TD>');



    DBMS_OUTPUT.PUT_LINE('</TR>');

  END LOOP;

  DBMS_OUTPUT.PUT_LINE('</TABLE>');
  DBMS_OUTPUT.PUT_LINE('<H1>Execution Plan Color Map for '|| sqlid ||'@'|| dbname ||'</H1>');

END;

